class Solution {
    private int[] result;
    public int[] constructDistancedSequence(int n) {
        int[] res = new int[2*n-1];
        result = Arrays.copyOf(res, res.length);
        helper(n, res);
        return result;
    }

    private void helper(int n, int[] acc) {
        if (n == 1) {
            int idx = -1;
            for (int i = 0; i < acc.length; i++) {
                if (acc[i] == 0) {
                    idx = i;
                    break;
                }
            }
            acc[idx] = 1;
            result = Arrays.copyOf(acc, acc.length);
            acc[idx] = 0;
            return;
        }

        for (int idx = 0; idx < acc.length - n; idx++) {
            if (acc[idx] == 0 && acc[idx+n] == 0) {
                acc[idx] = n;
                acc[idx+n] = n;
                if (isLarger(acc, result, n)) helper(n-1, acc);
                acc[idx] = 0;
                acc[idx+n] = 0;
            }
        }
    }

    private boolean isLarger(int[] a, int[] b, int n) {
        for (int i = 0; i < a.length; i++) {
            if (a[i] > b[i] || (a[i] == 0 && b[i] < n-1)) return true;
            else if ((a[i] != 0 && b[i] > a[i]) || (a[i] == 0 && b[i] > n-1)) return false;
        }
        return false;
    }
}
