class Solution {
    public int[] constructDistancedSequence(int n) {
        int[] result = new int[2 * n - 1];
        // result init to zero.
        Set<Integer> candidatesUsed = new HashSet<Integer>();
        helper(result,0,candidatesUsed,n);
        return result;
    }

    boolean valid(int[] result, int currentIndex, int candidate) {
        if(candidate == 1) return result[currentIndex] == 0;
        int N = result.length;
        int nextIndex = currentIndex + candidate;
        return nextIndex < N && result[nextIndex] == 0 && result[currentIndex] == 0;
    }

    void fill(int[] result, int currentIndex, int candidate) {
        if(candidate == 1) {
            result[currentIndex] = candidate;  
            return;
        }
        int N = result.length;
        int nextIndex = currentIndex + candidate;
        result[currentIndex] = candidate;
        result[nextIndex] = candidate;
    }

    void unfill(int[] result, int currentIndex, int candidate) {
        if(candidate == 1) {
            result[currentIndex] = 0;
            return;
        }
        int N = result.length;
        int nextIndex = currentIndex + candidate;
        result[currentIndex]= 0;
        result[nextIndex] = 0;
    }


    public boolean helper(int[] result, int index, Set<Integer> candidatesUsed,int n) {
        // greedily fill the leftmost number with a digit.
        int N = result.length;
        if(index == N) return true;
        // currentIndex will be less than N
        // currentIndex is filled with 0.
        for(int candidate=n;candidate>=0;candidate--) {
            if(candidatesUsed.contains(candidate)) continue;
            if(!valid(result,index,candidate)) continue;
            fill(result,index,candidate);
            //System.out.println(Arrays.toString(result));
            candidatesUsed.add(candidate);
            int recursionIndex = index+1;
            while(recursionIndex < N && result[recursionIndex]!=0) recursionIndex++;
            if(helper(result,recursionIndex,candidatesUsed,n)) return true;
            candidatesUsed.remove(candidate);
            unfill(result,index,candidate);
        }
        return false;
    }

}
