class Solution:
    def constructDistancedSequence(self, n: int) -> List[int]:
        N = (n * 2) - 1 # final length of solution
        def isValid(sln, curIdx, val):
            # check if we can place val at curIdx (and curIdx + val, if needed)
            if val == 1:
                return sln[curIdx] is None
            else:
                return (curIdx + val) < len(sln) and sln[curIdx] is None and sln[curIdx + val] is None
        def backtrack(sln, used):
            # find an open spot to place our value
            for tIdx in range(N):
                if sln[tIdx] is None:
                    break
            if tIdx == N - 1 and sln[-1] is not None: return sln[:] # all spots taken, we found a solution

            for i in range(n)[::-1]: # iterate in reverse for lexicographically largest sequence
                val = i + 1
                if val not in used and isValid(sln, tIdx, val):
                    # place val
                    used.add(val)
                    sln[tIdx] = val
                    if val != 1:
                        sln[tIdx+val] = val

                    v = backtrack(sln, used)
                    if v: return v

                    # backtrack: undo placement
                    sln[tIdx] = None
                    if val != 1:
                        sln[tIdx+val] = None
                    used.remove(val)
        return backtrack([None for _ in range(N)], set())
```

