class Solution {
    func orderlyQueue(_ s: String, _ k: Int) -> String {
        guard s.count > 1 else { return s }
        if k == 1 { // when `k` is one, return minimal rotation
            return (1..<s.count).reduce((
                minString: "{",  // brace-character has higher ASCII value than all letters
                s: Array(s)  // converts String `s` into array of characters for substring
            )) { data,i in 
                (
                    minString: min(data.minString, String(data.s[i...] + data.s[0..<i])), 
                    s: data.s
                )
            }.minString 
        } else {
            return String(s.sorted()) // when k>1, then minimal value will always be lexicographical sort
        }
    }
}
