class Solution {
public:
    string orderlyQueue(string s, int k) {
        if (k == 1)
        {
            deque<char> smallest(s.begin(), s.end());
            deque<char> rotate = smallest;

            int n = s.size();
            while (n > 0)
            {
                rotate.push_back(rotate.front());
                rotate.pop_front();
                if (rotate < smallest)
                {
                    smallest = rotate;
                }
                n --;
            }
            return string(smallest.begin(), smallest.end());
        }
        else
        {
            sort(s.begin(), s.end());
            return s;
        }
    }
};
```

