            sort(begin(s),end(s));
        } else
        {
            auto it = begin(s);
            auto en = end(s);
            auto it2 = min_element(it+1,en);            
            while(it2!=en && *it2<=*it)
            {
                int len = N- (it2 - begin(s));
                // &*it2 is a hack since the iterator version of the 
                // C++20 string_view constructor is not supported on this platform
                string_view curprefix(&*it, len), cursuffix(&*(it+len),N-len),
                            newprefix(&*it2,len), newsuffix(&*it, N-len);
