Suffix Array based smallest cyclic shift taken from https://cp-algorithms.com/string/suffix-array.html#toc-tgt-5

```
class Solution {
public:
    string orderlyQueue(string s, int k) {
        if (k >= 2) {
            std::sort(s.begin(), s.end());
            return s;
        }
        const int idx = sort_cyclic_shifts(s).front();
        return s.substr(idx) + s.substr(0, idx);
    }
private:
    std::vector<int> sort_cyclic_shifts(std::string const& s) {
        const int n = s.size();
        constexpr int ALPHABET = 256;

        vector<int> p(n), c(n), cnt(max(ALPHABET, n), 0);
        for (int i = 0; i < n; ++i) {
            cnt[s[i]]++;
        }
        for (int i = 1; i < ALPHABET; ++i) {
            cnt[i] += cnt[i-1];
        }
        for (int i = 0; i < n; ++i) {
            p[--cnt[s[i]]] = i;
        }

        c[p[0]] = 0;
        int classes = 1;
        for (int i = 1; i < n; i++) {
            if (s[p[i]] != s[p[i-1]]) {
                classes++;
            }
            c[p[i]] = classes - 1;
        }

        std::vector<int> pn(n), cn(n);
        for (int h = 0; (1 << h) < n; ++h) {
            for (int i = 0; i < n; i++) {
                pn[i] = p[i] - (1 << h);
                if (pn[i] < 0)
                    pn[i] += n;
            }
            std::fill(cnt.begin(), cnt.begin() + classes, 0);
            for (int i = 0; i < n; i++) {
                cnt[c[pn[i]]]++;
            }
            for (int i = 1; i < classes; i++) {
                cnt[i] += cnt[i-1];
            }
            for (int i = n-1; i >= 0; i--) {
                p[--cnt[c[pn[i]]]] = pn[i];
            }
            cn[p[0]] = 0;
            classes = 1;
            for (int i = 1; i < n; i++) {
                std::pair<int, int> curr = {
                    c[p[i]], c[(p[i] + (1 << h)) % n]
                };
                std::pair<int, int> prev = {
                    c[p[i-1]], c[(p[i-1] + (1 << h)) % n]
                };
                if (curr != prev) {
                    ++classes;
                }
                cn[p[i]] = classes - 1;
            }
            c.swap(cn);
        }
        return p;        
    }    
};
```

