class Solution {
public:
    string orderlyQueue(string s, int k, string res = "") {
        if(s.size() == 1 || k == 0) return s;
        if(k > 1){
            vector<int> l(26);
            for(auto i : s) l[i - 'a']++;
            for(int i = 0;i < 26;i++)
                for(int j = 0;j < l[i];j++)
                    res += i + 'a';
            return res;
        }
        res = s;
        for(int i = 0;i < s.size();i++){
            std::rotate(s.begin(), s.begin() + 1, s.end());
            res = min(res, s);
        }
        return res;
    }
};
