class Solution {
public:
    int lps(string& s, int start, int end)
    {
        if (start == end) return 1;
        if (start > end) return 0;

        if (s[start] == s[end]) return (2 + lps(s, start + 1, end - 1));
        int leaveLeft = lps(s, start + 1, end);
        int leaveRight = lps(s, start, end - 1);
        return max(leaveLeft, leaveRight);
    }
    int minInsertions(string s) 
    {
        int lpsLen = lps(s, 0, s.size() - 1);
        return (s.size() - lpsLen);
    }

};
```
