## TOP-DOWN
```
class Solution {
public:
    int lps(string& s, vector<vector<int>>&dp, int start, int end)
    {
        if (start == end) return 1;
        if (start > end) return 0;
        if (dp[start][end] != -1) return dp[start][end];

        if (s[start] == s[end]) return (2 + lps(s, dp, start + 1, end - 1)); //directly return 

        int leaveLeft = lps(s, dp, start + 1, end);
        int leaveRight = lps(s, dp, start, end - 1);
        return dp[start][end] = max(leaveLeft, leaveRight); //store the ans
    }
    int minInsertions(string s) 
    {
        int n = s.size();
        vector<vector<int>>dp(n, vector<int>(n, -1));
        int lpsLen = lps(s, dp, 0, n - 1);
        return (n - lpsLen);
    }

};
```
