class Solution {
    public int minInsertions(String s1) {
	    int n =s1.length();
        String s2 ="";
        for(int i=n-1; i>=0; i--) s2 += s1.charAt(i); // S2 = rev(S1) for L.P.S computation

        int dp[] = new int[n+1];
        for(int i=1; i<=n; i++){ // Generating L.C.S ~ Space Optimized!
            int cur[] = new int[n+1];
            for(int j=1; j<=n; j++){
                if(s1.charAt(i-1) == s2.charAt(j-1)) cur[j] = 1 + dp[j-1];
                else cur[j] = Math.max(dp[j], cur[j-1]);
            }
            dp = cur;
        } // Got our L.P.S

        // Now, min_operation = n - lps 
        return (n - dp[n]); // a c p c a 
    }
}
