class Solution {
public:
    //BRUTE FORCE= RECURSION
    int makePalindrome(int i, int j, string &s){
        if(i>=j) return 0;
        else if(s[i]==s[j]) return makePalindrome(i+1, j-1, s);
        else{
            int possibility1= makePalindrome(i+1, j, s);
            int possibility2= makePalindrome(i, j-1, s);

            return min(possibility1, possibility2)+1;
        }
    }
    int minInsertions(string s) {
        int n= s.size();
        return makePalindrome(0, n-1, s);
    }
};
