class Solution {
public:
    // Before reading this, I would highly recommend to take a rought notebook and draw the flow chart of these recursive calls, change the variables values and check for yourself on how all these recursive values are adding up finally and what value is actually getting returned.
// It would be a little tedious process but it will help u in Visualising the recursion tree.
// Solve this code for "mbadm"
    int minInsert(string& s,int i,int j,vector<vector<int>>& dp) {
        if(i>=j) { // base case
            return 0;
        }
        if(dp[i][j]!=-1) { // if we have the value already calculated , just return that value
            return dp[i][j];
        }
        if(s[i]==s[j]) { 
            dp[i][j]=minInsert(s,i+1,j-1,dp); // call recursion by decreasing j and increasing i and save the result of dp[i][j] which will be calculated recursively
            return dp[i][j]; // return the result that we got above 
        }
        // Now if the chars at s[i] and s[j] are not same/palindrome, NOW WE HAVE 2 DECISION, EITHER ADD A CHARACTER ON THE LEFT SIDE(i pointer) OR ADD A CHARCTER on the RIGHT SIDE(j pointer)
        int dec1=minInsert(s,i+1,j,dp)+1; // ADDING ONE CHAR ON THE LEFT AND INCREMENT THE DECISION+1 AND calling recursively ON i+1 and j
        int dec2=minInsert(s,i,j-1,dp)+1; //ADDING ONE CHAR ON THE RIGHT AND INCREMENT THE DECISION+1 AND calling recursively ON i and j+1
        dp[i][j]=min(dec1,dec2); // SAVING THE MINIMUM OF THE 2 DECISION ON dp[i][j]
        return min(dec1,dec2); // RETURNING THE MINIMUM DECISION THAT WE GOT
    } 
    int minInsertions(string s) {
        int len=s.length(); // calculating length
        vector<vector<int>>dp(len,vector<int>(len,-1)); // creating 2d vector of size len*len and filling with -1
        return minInsert(s,0,len-1,dp); // calling recursively 
    }
};
