#define vi vector<int>
#define vvi vector<vi>

class Solution {
public:
    int solveMem(string& a, string& b, int i, int j, vvi& dp)   {
        if(i == a.length() || j == b.length())  return 0;
        if(dp[i][j] != -1)  return dp[i][j];
        if(a[i] == b[j])    dp[i][j] = 1 + solveMem(a, b, i+1, j+1, dp);
        else    dp[i][j] = max(solveMem(a, b, i+1, j, dp), solveMem(a, b, i, j+1, dp));
        return dp[i][j];
    }

    int solveTab(string& a, string& b)  {
        vvi dp(a.length() + 1, vi(a.length() + 1, 0));

        for(int i=a.length()-1; i>=0; i--)  {
            for(int j=b.length()-1; j>=0; j--)  {
                if(a[i] == b[j])    dp[i][j] = 1 + dp[i+1][j+1];
                else    dp[i][j] = max(dp[i+1][j], dp[i][j+1]);
            }
        }

        return dp[0][0];
    }


    int minInsertions(string s) {
        string rev = s;
        reverse(rev.begin(), rev.end());
        vvi dp(s.length(), vi(s.length(), -1));
        return s.length() - solveMem(s, rev, 0, 0, dp);

        // return s.length() - solveTab(s, rev);
    }
};
