	int vec[501][501];

	int fun(string &s1, string &s2, int n1, int n2)
	{
		if(n1 == 0 || n2 == 0)
			return 0;

		if(vec[n1 - 1][n2 - 1] != -1)
			return vec[n1 - 1][n2 - 1];

		if(s1[n1 - 1] == s2[n2 - 1])
			return vec[n1 - 1][n2 - 1] = (1 + fun(s1, s2, n1 - 1, n2 -1));
		else
			return vec[n1 - 1][n2 - 1] = max(fun(s1, s2, n1 - 1, n2), fun(s1, s2, n1, n2 - 1));

	}

	int minInsertions(string s) {
		string srev = s;
		reverse(s.begin(), s.end());

		for(int i = 0; i < 501; i++)
			for(int j = 0; j < 501; j++)
				vec[i][j] = -1;

		fun(srev, s, s.length(), s.length());

		return s.length()- vec[s.length() - 1][s.length() - 1];

	}
};

