package main

func minInsertions(s string) int {
    n := len(s)
    dp := make([][]int, n+1)
    for i := 0; i <= n; i++ {
        dp[i] = make([]int, n+1)
    }
    r := reverse(s)
    for i := 0; i <= n; i++ {
        for j := 0; j <= n; j++ {
            if i == 0 || j == 0 {
                dp[i][j] = 0
            } else if s[i-1] == r[j-1] {
                dp[i][j] = dp[i-1][j-1] + 1
            } else {
                dp[i][j] = max(dp[i-1][j], dp[i][j-1])
            }
        }
    }
    return n - dp[n][n]
}

func reverse(s string) string {
    n := len(s)
    r := make([]byte, n)
    for i := 0; i < n; i++ {
        r[i] = s[n-i-1]
    }
    return string(r)
}

