    def minInsertions(self, s: str) -> int:
        def lcp(n,s,st_r,prev):
            for i in range(1,n+1):
                temp=[0]*(n+1)
                for j in range(1,n+1):
                    if s[i-1]==st_r[j-1]:
                        temp[j]=1+prev[j-1]
                    else:
                        temp[j]=max(prev[j],temp[j-1])
                prev=temp
            return (n-prev[n])

        n=len(s)
        st_r=s[::-1]
        prev=[0]*(n+1)
        return lcp(n,s,st_r,prev)
