class Solution {
public:
    int minInsertions(string s) {
        string t = s;
        reverse(t.begin(), t.end());
        vector<vector<int>> dp(s.length()+1, vector<int> (s.length()+1, -1));
        return s.length() - lcs(s.length(), s.length(), s, t, dp);
    }

    int lcs(int i, int j, string s, string t, vector<vector<int>> dp){
        if(i==0 || j==0)
            return 0;

        if(dp[i][j] != -1)
            return dp[i][j];

        if(s[i-1] == t[j-1])
            return dp[i][j] = 1+lcs(i-1, j-1, s, t, dp);
        else
            return dp[i][j] = max(lcs(i, j-1, s, t, dp), lcs(i-1, j, s, t, dp));
    }
};
```

#  Approach 3 : Tabulation (Accepted)
