class Solution {
public:
    //lcs
    int lcs(string s1, string s2){
        int n = s1.size();
        int m = s2.size();
        vector<vector<int>> dp(n+1, vector<int>(m+1, -1));
        for(int i=0; i<=n; i++){
            dp[i][0] = 0;

        }
        for(int i=0; i<=m; i++){
            dp[0][i] = 0;

        }

        for(int i=1; i<=n; i++){
            for(int j = 1; j<=m; j++){
                if(s1[i-1] == s2[j-1])
                    dp[i][j] = 1+ dp[i-1][j-1];
                else{
                    dp[i][j] = max(dp[i-1][j], dp[i][j-1]);
                }
            }
        }

        return dp[n][m];
    }


    //longest palindromic subsequence
    int lps(string s){
        string rev = s;
        reverse(s.begin(), s.end());

        return lcs(s, rev);
    }


    // The difference between the size of the string and the length of lps will give us the number of chars that needs to be inserted to make the string a palindrome

    int minInsertions(string s) {
        return s.size() - lps(s);
    }
};
