class Solution {
public:
    int minInsertions(string s) {
        int n=s.length();
        string s2=s;
        reverse(s.begin(),s.end());
        int dp[n+1][n+1];
        memset(dp,0,sizeof(dp));
        int ans=INT_MIN;
		//top-down dp for finding LPS
        for(int i=1;i<=n;i++){
            for(int j=1;j<=n;j++){
                if(s[i-1]==s2[j-1]){
                    dp[i][j]=1+dp[i-1][j-1];
                    ans = max(ans,dp[i][j]);
                }else dp[i][j]=max(dp[i][j-1],dp[i-1][j]);
            }
        }
        return ans==INT_MIN ? (n%2==0 ? n : n-1) : n-ans;
    }
};
