class Solution {
public:
    int f(int l,int r,string &s,vector<vector<int>>&dp){
      if(l>=r) return 0;

      if(dp[l][r]!=-1) return dp[l][r];

      int x=1e9,y=1e9;

      if(s[l]==s[r]){
        x=f(l+1,r-1,s,dp);
      }
      else{
        y=1+min(f(l+1,r,s,dp),f(l,r-1,s,dp));
     }

      return dp[l][r]=min(x,y);
