```
int longestPalindromicSubsequenceMemo(string& s, int i, int j, vector<vector<int>>& memo) {
        if(i > j)
            return 0;
        if(i == j)
            return 1;
        if(s[i] == s[j] && i+1 == j)
            return 2;

        if(memo[i][j] != -1)
            return memo[i][j];
        if(s[i] == s[j]) {
            memo[i][j] =  2 + longestPalindromicSubsequenceMemo(s, i+1, j-1, memo);
        } else {
            memo[i][j] =  max(longestPalindromicSubsequenceMemo(s, i+1, j, memo), longestPalindromicSubsequenceMemo(s, i, j-1, memo));
        }
        return memo[i][j];

    }

    int minInsertions(string s) {
        int n = s.length();
        vector<vector<int>> memo(n, vector<int>(n, -1));
        return n-longestPalindromicSubsequenceMemo(s, 0, n-1, memo);
    }

    /** LCS way*/
    int minInsertions(string s) {
        int n = s.length();
        vector<vector<int>> dp(n+1, vector<int>(n+1, 0));
        string rev = s;
        reverse(rev.begin(), rev.end());
        for(int i = 1; i <=n; i++) {
            for(int j = 1; j <=n; j++) {
