class Solution {
    public int minInsertions(String s) {
        if (s == null || s.length() == 0) return 0;
        int[][] min = new int[s.length()][s.length()];
        for (int[] row : min) Arrays.fill(row, -1);
        return dp(s, 0, s.length() - 1, min);
    }
    private int dp(String s, int l, int r, int[][] min) {
        if (l >= r) return 0;
        if (min[l][r] >= 0) return min[l][r];
        min[l][r] = s.charAt(l) == s.charAt(r) ? dp(s, l + 1, r - 1, min) : Math.min(dp(s, l + 1, r, min), dp(s, l, r - 1, min)) + 1;
        return min[l][r];
    }
}
