class Solution:
    def minInsertions(self, s: str) -> int:
        n=len(s)
        s2=s[-1::-1]
        prev=[0]*(n+1)
        for i in range(1,n+1):
            curr=[0]*(n+1)
            for j in range(1,n+1):
                if s[i-1]==s2[j-1]:
                    curr[j]=1+prev[j-1]
                else:
                    curr[j]=max(prev[j],curr[j-1])
            prev=curr[:]
        return n-prev[n]    
