class Solution {
    int help(int i,int j,vector<vector<int>>& dp,string & s){  // i from left and j form right
        if(i>=j){  //pass or equal then 0
            return 0;
        }
        if(dp[i][j]!=-1){
            return dp[i][j];
        }
        int ans=0;
        if(s[i]==s[j]){
            ans=help(i+1,j-1,dp,s);  //equal hai to insert karne ki need nhi
        }
        else{
            ans=1+min(help(i+1,j,dp,s),help(i,j-1,dp,s)); //equal nhi to check karke mininum insert kisme kare
        }
        return dp[i][j]=ans;
    }
public:
    int minInsertions(string s) {
        int n=s.size();
        vector<vector<int>>dp(n,vector<int>(n,-1));
        return help(0,n-1,dp,s);
    }
};
