class Solution {
public:
    int minInsertions(string s) {
        int n = s.size();
        return n - lcs(s, n);
    }

private:
    int lcs(string& s, int& n) {
        vector<int> prev(n + 1, 0);
        vector<int> curr(n + 1, 0);

        for (int i = 1; i <= n; i++) {
            for (int j = 1; j <= n; j++) {
                if (s[i-1] == s[n-j]) curr[j] = 1 + prev[j-1];
                else curr[j] = max(prev[j], curr[j-1]);
            }
            prev = curr;
        }

        return prev[n];
    }
};
