class Solution {
public:

    int permInsert(string& s, int l, int r, vector<vector<int>>& dp) {
        if (l >= r) return 0; // base case
        if (dp[l][r] != -1) return dp[l][r]; //dp, have we solved for this comparison before?

        int res; // if no dp, solve for this comparison
        if (s[l] == s[r]) res = permInsert(s, l + 1, r - 1, dp);
        else res = 1 + min(permInsert(s, l, r - 1, dp), permInsert(s, l + 1, r, dp));

        dp[l][r] = res; //store result
        return dp[l][r]; //return
    }

    int minInsertions(string s) {
        vector<vector<int>> dp(s.length(), vector<int>(s.length(), -1));
        return permInsert(s, 0, s.length() - 1, dp);
    }
};
