        $n = strlen($s);
        $r = strrev($s);
        $a = array_fill(0, 2, array_fill(-1, $n + 1, 0));

        for ($i = 0; $i < $n; $i++) {
            $c = $i % 2;
            for ($j = 0; $j < $n; $j++) {
