class Solution {
    public int minInsertions(String s) {
        int n=s.length();
        int[][] dp=new int[n][n];   //dp to store if cases with current start and end character have been already handled
        char[] ar=s.toCharArray();
        return helper(ar,0,n-1,dp);
    }
    public int helper(char[] ar,int start,int end,int[][] dp){
        if(dp[start][end]!=0) return dp[start][end];   //if handled this case return val

        //iterating until to outer characters dont match
        while(ar[start]==ar[end] && start<end){
            start++;
            end--;
        }
        //if every character in the current range match return 0
        if(start>=end) return 0;

