class Solution:
    def minInsertions(self, s: str) -> int:

        @lru_cache(None)
        def dp(l, r): # return min step
            if l >= r:
                return 0

            if s[l] == s[r]:
                return dp(l+1, r-1)

            else: # add left or right
                t1 = dp(l+1, r) + 1
                t2 = dp(l, r-1) + 1
                return min(t1, t2)

        return dp(0, len(s) - 1)
