class Solution {
public:
int dp[501][501]; // States - start index and end index

int solve(string& s, int ind1, int ind2){
    if(ind1 == ind2) return 0;

    if(ind2 - ind1 == 1){
        if(s[ind1] == s[ind2]) return 0;
        else return 1;
    }

    if(dp[ind1][ind2] != -1) return dp[ind1][ind2];

    int ans;

    ans = 1 + min(solve(s,ind1,ind2-1), solve(s,ind1+1,ind2));

    if(s[ind1] == s[ind2]) ans = min(ans, solve(s,ind1+1,ind2 -1));

    return dp[ind1][ind2] = ans;
 }
    int minInsertions(string s) {
    memset(dp,-1,sizeof(dp));

    return solve(s,0,s.size()-1);

    }
};
