                    }
                }
                if (S.charAt(j - 1) == 'I') {
                    dp[i][j] += dp[i][j - 1];
                    dp[i][j] %= M;
                }
            }
        }
        return (int)dp[0][n];
    }
    private int nCk(int n, int k, int[][] nCkMemo) {
        if (k == 0 || k == n) {
            return 1;
        }
        if (nCkMemo[n][k] == 0) {
            nCkMemo[n][k] = (nCk(n - 1, k, nCkMemo) + nCk(n - 1, k - 1, nCkMemo)) % M;
        }
        return nCkMemo[n][k];
    }
}
```
