class Solution {
    public int numPermsDISequence(String s) {
	int n = s.length();
	int[][] dp = new int [n+2][2];
	int mod = 1000000007;
	dp[1][0] = 1;
	for(int i = 1; i<=n; i++){
		if(s.charAt(i-1)=='I') {
			for(int j = 0; j<=i; j++) dp[j+1][i&1]=(dp[j][(i+1)&1] + dp[j][i&1])%mod;  
		} else {
			for(int j = 0; j<=i; j++) dp[j+1][i&1]=((dp[i][(i+1)&1] + mod - dp[j][(i+1)&1])%mod + dp[j][i&1])%mod;
		} 
	}
	return dp[n+1][n&1];
}
}
