        if(s[index]=='I'){
            int previous=prev;
            for(int i=previous+1;i<=n;i++){
                if(visited[i]!=-1){
                    curr.push_back(i);
            visited[i]=-1;
            ans=(ans%mod+hope(index+1,i,curr,visited,s,n,dp)%mod)%mod;
            curr.pop_back();
            visited[i]=0;
                }
            }
