            visited[i]=-1;
            ans=(ans%mod+hope(index+1,i,curr,visited,s,n,dp)%mod)%mod;
            curr.pop_back();
            visited[i]=0;
                }
            }
        }
    }
    return dp[index+1][prev+1]=ans%mod;
