class Solution {
public:
int mod=1e9+7;
    int numPermsDISequence(string s) {
        int n=s.size();
        vector<vector<int>> dp(n+1,vector<int>(n+1));
        for(int i=0;i<=n;i++){
            dp[0][i]=1+i;
