                    ans+=1LL*dp[i-1][j];
                    ans=(ans+mod)%mod;
                }
                if(j>0) ans+=dp[i][j-1];
                ans%=mod;
                dp[i][j]=ans;
            }
        }
        return dp[n][0];
    }
};
