class Solution:
    def numPermsDISequence(self, s):
        n, mod = len(s), 10**9+7

        @lru_cache(None)
        def dfs(i,val):
            if i == n:
                return 1

            if s[i] == "D":
                if val == 0: return 0
                return dfs(i,val-1) + dfs(i+1,val-1)

            if s[i] == "I":
                if val == n-i: return 0
                return dfs(i,val+1) + dfs(i+1,val)

        return sum([dfs(0,j) for j in range(n+1)])%mod

```

