class Solution {
public:
    vector<int> vis;
    int dp[202][202];
    int mod = 1000000007;
    int recur(string &s, int i, int n,int idx){
        if(idx==n-1){
            return 1;
        }
        if(dp[idx][i]!=-1)
            return dp[idx][i];

        int res = 0;
        if(s[idx]=='D'){
            for(int j=i-1;j>=0;j--){
                if(vis[j])  continue;
                vis[j] = 1;
                res = (res + (recur(s,j,n,idx+1))%mod)%mod;
                vis[j] = 0; 
            }
        }
        else{
            for(int j=i+1;j<n;j++){
                if(vis[j])  continue;
                vis[j] = 1;
                res = (res + (recur(s,j,n,idx+1))%mod)%mod;
                vis[j] = 0;
            }
        }
        return dp[idx][i] = res;
    }

    int numPermsDISequence(string s) {
        int n = s.size()+1;
        vis.resize(n,0);
        for(int i=0;i<202;i++){
            for(int j=0;j<202;j++){
                dp[i][j] = -1;
            }
        }
        int ans = 0;
        for(int i=0;i<n;i++){
            vis[i] = 1;
            ans = (ans + (recur(s,i,n,0))%mod)%mod;
            vis[i] = 0;
        }
        return ans;
    }
};
```

