class Solution:
    def numPermsDISequence(self, s: str) -> int:
        n,ans = len(s),0
        mod = 1_00_00_00_000 + 7
        memo = [[-1] * 201 for i in range(201)]
        def dp(ind,prevNum):
            if ind < 0:
                return 1
            if memo[ind][prevNum] != -1:
                return memo[ind][prevNum]
            val = 0
            if s[ind] == 'D':
                for more in range(prevNum+1,n+1):
                    if more not in vis:
                        vis.add(more)
                        val += dp(ind-1,more)
                        vis.remove(more)
            else:
                for less in range(prevNum):
                    if less not in vis:
                        vis.add(less)
                        val += dp(ind-1,less)
                        vis.remove(less)
            memo[ind][prevNum] = val
            return memo[ind][prevNum]
        for i in range(n+1):
            vis = set([i])
            ans += dp(n-1,i)
        return ans % mod

