var numPermsDISequence = function(s) {
    let mod = 10**9+7,res = 0
    const dp = new Array(s.length+1).fill().map(()=>new Array)
    dp[0][0]=1
    for(i=1;i<s.length+1;i++) {
        if(s[i-1]==='D') {
            dp[i][i]=0
            for(j=i-1;j>=0;j--) {
                dp[i][j]=(dp[i][j+1]+dp[i-1][j])%mod
            }
        } else {
            dp[i][0]=0
            for(j=1;j<=i;j++) {
                dp[i][j]=(dp[i][j-1]+dp[i-1][j-1])%mod
            }
        }
    }
    dp[s.length].map((e)=>{res+=e%mod})
    return res%mod
};
