class Solution {
    private static final int mod = 1000000007;
    int[] seen = null;
    Integer[][] dp = null;
    public int numPermsDISequence(String s) {
        dp = new Integer[s.length()][s.length()+1];
        seen = new int[s.length()+1];
        int count = 0;
        for(int i=0; i <= s.length(); i++) {
            seen[i] = 1;
        	count = count % mod + numPerms(s, 0, i) % mod;
            seen[i] = 0;
        }
        return count % mod;
    }
    private int numPerms(String s, int j, int p){
        if(j == s.length())
            return 1;
        if(dp[j][p] != null) return dp[j][p];
        char ch = s.charAt(j);
        int count = 0;
        if(ch == 'D'){
            for(int i=p-1; i >= 0; i--){
                if(seen[i] == 1)
                    continue;
                seen[i] = 1;
                count = count % mod + numPerms(s, j+1, i)  % mod;
                seen[i] = 0;
            }
        }else{
            for(int i=p+1; i <= s.length(); i++){
                if(seen[i] == 1)
                    continue;
                seen[i] = 1;
                count = count % mod + numPerms(s, j+1, i) % mod;
                seen[i] = 0;
            }
        }
        dp[j][p] = count % mod;
        return dp[j][p];
    }
}

