class Solution {
public:
    int mod = 1e9 + 7;
    // unordered_set<int>st;
    int perm(int i, int prev, int n, string &s, vector<vector<int>>&dp, vector<bool>&vis)
    {
        //cout<<"HERE"<<i<<"\n";
        if(i<0)
        {
            return 1;
        }
        if(dp[i][prev] != -1)
        {
            return dp[i][prev];
        }

        int ways = 0;

        if(i == n)
        {
            for(int j=0; j<=n;j++)
            {
                int x = j;
                vis[x] = true;
                //cout<<x<<"\n";
                ways = (ways + perm(i-1, x, n, s, dp, vis))%mod;
                //cout<<ways<<" "<<x<<"\n";
                vis[x] = false;
            }
        }
        else
        {
            if(s[i] == 'D')
            {
                for(int j=0; j<=n; j++)
                {
                    if(vis[j] == true) continue;
                    int x = j;
                    if(prev < x)
                    {
                        vis[x] = true;
                        ways = (ways + perm(i-1, x,n, s, dp, vis))%mod;
                        vis[x] = false;
                    }

                }
            }
            else
            {
                for(int j=0; j<=n; j++)
                {
                    if(vis[j] == true) continue;
                    int x = j;
                    if(prev > x)
                    {
                        vis[x] = true;
                        ways = (ways + perm(i-1, x,n, s, dp, vis))%mod;
                        vis[x] = false;
                    }
                }
            }
        }
        return dp[i][prev] = ways;
    }


