class Solution {
public:

    int dp[201][201];
    int m = 1e9+7;
    int helper(string &s, int i, vector<bool> &visit, int start){
        if(i==s.length()){
            return 1;
        }

        if(dp[i][start]!=-1){
            return dp[i][start];
        }
        if(s[i]=='D'){
            int ans = 0;
            for(int j = 0;j < start;j++){

                if(visit[j]==false){
                    visit[j]=true;
                    ans = (ans%m + helper(s,i+1,visit,j)%m)%m;
                    visit[j]=false;
                }
            }
            return dp[i][start] = ans;
        }
        else{
            int ans = 0;
            for(int j = start+1;j <= s.length();j++){

                if(visit[j]==false){
                    visit[j]=true;
                    ans = (ans%m + helper(s,i+1,visit,j)%m)%m;
                    visit[j]=false;
                }
