function numPermsDISequence(s) {
    let mod = 1e9 + 7, dp = [[1], []], res = 0

    for (let i = 1; i <= S.length; i++) {
        for (let j = 0; j <= i; j++) {
            let l = 0,  r = j
            if (s.charAt(i-1) === 'D') {
                l = j, r = i
            }
            dp[1][j] = 0
            for (let k = l; k < r; k++) {
                dp[1][j] += dp[0][k]
                dp[1][j] %= mod
            }
        }
        [dp[0], dp[1]] = [dp[1], dp[0]]
    }

    for (let cnt of dp.shift()) {
        res += cnt
        res %= mod
    }
    return res
}
