```c++
class Solution {
public:
    int n;
    string s;
    int mod = 1e9 + 7;
    map<pair<int,int>,int>dp;
    int countways(int id,int prev,vector<bool>&taken){

        if(id==n+1){
            return 1;
        }
        if(dp.find({id,prev})!=dp.end()) return dp[{id,prev}];
        int ans = 0;
        if(s[id-1]=='D'){
            for(int i = 0;i<=prev;i++){
                if(taken[i]==true) continue;
                taken[i] = true;
                ans+=countways(id+1,i,taken)%mod;
                ans%=mod;
                taken[i] = false;
            }
        }
        else{
            for(int i = prev+1;i<=n;i++){
                if(taken[i]==true) continue;
                taken[i] = true;
                ans+=countways(id+1,i,taken)%mod;
                ans%=mod;
                taken[i] = false;
            }
        }
        return dp[{id,prev}] = ans%mod;
    }
    int numPermsDISequence(string _s) {
        s = _s;
        n = s.size();
        vector<bool>taken(n+1,false);
        int ans = 0;
        for(int i = 0;i<=n;i++){
            taken[i] = true;
            ans+=countways(1,i,taken)%mod;
            ans%=mod;
            taken[i] = false;
        }
        return ans;
    }
};
