class Solution {
    int MOD = 1000000007;
    Map<Integer, Integer> cache;
    public int numPermsDISequence(String s) {
        cache = new HashMap<>();
        return dpRec(s, 0, 0, s.length(), new boolean[s.length()+1]);
    }
    private int dpRec(String s, int i, int x, int y, boolean[] vis) {
        if(i == s.length()){
            int j;
            for(j = x; j <= y; j++) if(vis[j] == false) break;
            return (j <= y) ? 1 : 0;
        } 
        int key = i*40000 + x*200 + y;
        if(cache.containsKey(key)) return cache.get(key);
        int count = 0;
        for(int j = x; j <= y; j++) {
            if(vis[j] == false) {
                vis[j] = true;
                count += (s.charAt(i) == 'D') ? dpRec(s, i+1, 0, j-1, vis) : dpRec(s, i+1, j+1, s.length(), vis);
                count = count%MOD;
                vis[j] = false;
            }
        }
        cache.put(key, count);
        return count;
    }
}
