from functools import lru_cache
class Solution:
    def numPermsDISequence(self, S: str) -> int:
        n = len(S) + 1
        @lru_cache(maxsize = 40000)
        def dfs(lastPos, pos):
            total = 0
            if pos == 0:
                for i in range(n):
                    total += dfs(i, pos + 1)
                return total%1000000007
            letter = S[pos - 1]
            if pos == len(S):
                if letter == 'I':
                    return 1 - lastPos
                else:
                    return lastPos
            else:
                if letter == 'I':
                    for i in range(lastPos, n-pos):
                        total += dfs(i, pos + 1)
                else:
                    for i in range(lastPos):
                        total += dfs(i, pos + 1)

                return total%1000000007

        return dfs(0,0)%1000000007
