///v2[i][j] stores value of i!/((i-j)!*j!) 
///v1[i][j] stores permutations for DI seq from j to j+i-1 
#define ll long long
class Solution {
public:
    ll p = 1e9+7;
    int numPermsDISequence(string s){
        ll n = s.size();
        vector < vector < ll > > v1( n+1 , vector<ll> ( n+1 , 1 ) );
        vector < vector < ll > > v2 = v1;
        for( int i = 1 ; i <= n ; i++) { 
            for( int j = 0 ; j <= i ; j++) {
                if( j==0 || j==i ) v2[i][j] = 1 ;
                else v2[i][j] = ( v2[i-1][j-1] + v2[i-1][j] ) % p;
            }
        }
        for(ll i = 2 ; i <= n ; i++) {
            for(ll j = 0 ; j+i-1 < n ; j++) {
                v1[i][j] = 0 ;
                for(ll k = j ;k < i + j -1 ; k++) {
                    if( s[k] == 'I' && s[k+1] == 'D' ) {
                        v1[i][j] = ( v1[i][j] + ( ( v2[i][k+1-j] * v1[k-j][j] ) %p ) * v1[i+j-k-2][k+2] ) % p;
                    }
                }
                if( s[j] == 'D' ) v1[i][j] = ( v1[i][j] + v1[i-1][j+1] ) %p;
                if( s[i+j-1] == 'I' ) v1[i][j] = ( v1[i][j] + v1[i-1][j] ) %p;
            }
        }
        return v1[n][0];
    }
};

