const int mod = 1e9+7;

class Solution {
public:
    int numPermsDISequence(string S) {
        int dp[205][205];
        memset(dp , 0 ,sizeof dp) ;
        int n = S.length();
        for(int j = 1 ; j <= n + 1  ; ++j) dp[1][j] = 1;

        for(int i = 2 ; i <= n + 1  ; ++i){
           // cout << " i is " << i << endl ; 
            if(S[i - 2] == 'D'){
                /// p[i] > p[i+1]
                int sum = 0;
                for(int j = n - i + 2 ; j >= 1 ; --j){
                    sum = sum + dp[i-1][j+1];
                    sum = sum % mod ;
                    dp[i][j] = sum ;
                }
            }
            else if(S[i-2] == 'I'){
                /// p[i] < p[i+1]
                int sum = 0;
                for(int j = 1 ; j <= n - i + 2 ; ++j){
                    sum = sum + dp[i-1][j];
                    sum = sum % mod ;
                    dp[i][j] = sum ;
                }
            }

        }
        return dp[n+1][1] ;
    }
};

